import axios, { AxiosResponse } from "axios"

const baseURL: string = "http://localhost:4000"

export const countWords = async (
  formData: IURL
): Promise<AxiosResponse<ApiDataType>> => {
  try {
    //const todo: Omit<ITodo, "_id"> = {
    //  name: formData.name,
    //  description: formData.description,
    //  status: false,
    //}
//    console.log(url)
    const url : IURL = {
      url: formData.url
    }
    const countWordsResp: AxiosResponse<ApiDataType> = await axios.post(
      baseURL + "/countwords", 
      url
    );
    return countWordsResp
  } catch (error) {
    throw new Error("Issue with addTodo")
  }
}


export const sortWords = async (): Promise<AxiosResponse<ApiDataType>> => {
  try {
    console.log(baseURL)
    const todos: AxiosResponse<ApiDataType> = await axios.get(
      baseURL + "/sortwords"
    )
    return todos
  } catch (error) {
    throw new Error("Issue with getTodos")
  }
}
